function renderCaseAlg(f,e,g,d){var b="";var a=getUses(f);if(a.length>0){a=" <sup>"+a+"</sup>"}var c="";if(f.status<1||f.uses.length<1){c=" inactive"}if(d>=IPAD_LANDSCAPE){b+="<tr>";b+='<td class="'+e+c+'">'+f.alg+a+"</td>";b+='<td class="desc'+c+'">'+replaceAbbr(f.desc)+"</td>";b+="</tr>"}else{if(g){b+="<tr><td>&nbsp;</td></tr>"}b+='<tr><td class="'+e+c+'">'+f.alg+a+"</td></tr>";b+='<tr><td class="'+e+c+'"><em>'+replaceAbbr(f.desc)+"</em></td></tr>"}return b}function renderCaseAlgs(b,d){var a="";var c=1;for(var g=0;g<b.algs.length;g++){var e=b.algs[g];if(e!=null){if(e.hasOwnProperty("name")){a+="<h3>"+e.name+"</h3>"}else{a+="<h3>Alg "+c+"</h3>"}switch(e.status){case 0:a+='<p class="inactive">I don\'t use this algorithm yet.</p>';break;case -1:a+='<p class="inactive">I don\'t use this algorithm nowadays.</p>';break}a+="<table>";a+=renderCaseAlg(e,"alg",false,d);if(e.hasOwnProperty("vars")){for(var h=0;h<e.vars.length;h++){var f=e.vars[h];if(f!=null){a+=renderCaseAlg(f,"var",true,d)}}}a+="</table>";c++}}return a}function renderCaseLinks(h,a){var d="";d+="<h3>Links</h3>";var f=h.wiki;var c=algSet.header.id;if(f!=""){if(h.wiki.indexOf("#")>=0){c=algSet.header.id}else{c=h.wiki}}var e=h.algdb;var k=algSet.header.id;if(e!=""){k=h.algdb}for(var b=0;f==""&&b<algSet.views.length;b++){var j=algSet.views[b];if(j!=null){if(j.hasOwnProperty("groups")){for(var i=0;i<j.groups.length;i++){var g=j.groups[i];if(g!=null){if(g.hasOwnProperty("wiki")){if(g.cases.indexOf(h.id)>=0){f=g.wiki;if(g.wiki.indexOf("#")>=0){c=algSet.header.id}else{c=g.wiki}}}}}}}}d+='<p>SpeedSolving Wiki: <a class="clicky" href="'+algSet.header.wiki+f+'" target="_blank">'+c+"</a><p>";d+='<p>AlgDb.Net: <a class="clicky" href="'+algSet.header.algdb+e+'" target="_blank">'+k+"</a><p>";return d}function renderCase(b,h){var c="";var g="128";var a=getCaseIds();c+='<h1><span class="menu-btn">&#9776;</span> '+algSet.header.id+" - "+b+' <a class="clicky" href="#random">&#8230;</a></h1>';c+=header(GALAXY_S3_PORTRAIT);if(a.indexOf(b)>=0){var e=algSet.cases[a.indexOf(b)];if(e.name!=b){c+="<h2>"+e.name+"</h2>"}document.title=algSet.header.id+" "+b;var d=algSet.header.hasOwnProperty("css")?algSet.header.css:algSet.header.id;var f=e.style!=null?e.style:"";c+='<p><i class="'+f+" s"+g+"-"+d.toLowerCase()+" s"+g+"-"+e.image.toLowerCase()+'"></i></p>';if(e.desc){c+="<p>"+replaceAbbr(e.desc)+"</p>"}c+="<p>Probability = "+e.prob+"</p>";c+=renderCaseAlgs(e,h);c+=renderCaseLinks(e,h)}else{c+="Missing case "+b}c+=footer();document.getElementById("container").innerHTML=c};