function renderCaseAlg(g,f,h,b){var c="";var a="";if(g.uses.length>0){a+=" <sup>";for(var d=0;d<g.uses.length;d++){if(d>0){a+=", "}a+=g.uses[d]}a+="</sup>"}var e="";if(g.status<1||g.uses.length<1){e=" inactive"}if(b>=TABLET_LANDSCAPE){c+="<tr>";c+='<td class="'+f+e+'">'+g.alg+a+"</td>";c+='<td class="desc'+e+'">'+replaceAbbr(g.desc)+"</td>";c+="</tr>"}else{if(h){c+="<tr><td>&nbsp;</td></tr>"}c+='<tr><td class="'+f+e+'">'+g.alg+a+"</td></tr>";c+='<tr><td class="'+f+e+'"><em>'+replaceAbbr(g.desc)+"</em></td></tr>"}return c}function renderCaseAlgs(c,a){var b="";var d=1;for(var g=0;g<c.algs.length;g++){var e=c.algs[g];b+="<h3>Alg "+d+"</h3>";switch(e.status){case 0:b+='<p class="inactive">I don\'t use this algorithm yet.</p>';break;case -1:b+='<p class="inactive">I don\'t use this algorithm nowadays.</p>';break}b+="<table>";b+=renderCaseAlg(e,"alg",false,a);if(e.hasOwnProperty("vars")){for(var h=0;h<e.vars.length;h++){var f=e.vars[h];b+=renderCaseAlg(f,"var",true,a)}}b+="</table>";d++}return b}function renderCaseLinks(i,h){var d="";d+="<h3>Links</h3>";var f=i.wiki;var c=algSet.header.id;if(f!=""){if(i.wiki.indexOf("#")>=0){c=algSet.header.id}else{c=i.wiki}}var e=i.algdb;var l=algSet.header.id;if(e!=""){l=i.algdb}for(var b=0;f==""&&b<algSet.views.length;b++){var k=algSet.views[b];if(k.hasOwnProperty("groups")){for(var j=0;j<k.groups.length;j++){var g=k.groups[j];if(g.hasOwnProperty("wiki")){for(var a=0;a<g.cases.length;a++){if(g.cases.indexOf(i.id)>=0){f=g.wiki;if(g.wiki.indexOf("#")>=0){c=algSet.header.id}else{c=g.wiki}}}}}}}d+='<p>SpeedSolving Wiki: <a href="'+algSet.header.wiki+f+'" target="_blank">'+c+"</a><p>";d+='<p>AlgDb.Net: <a href="'+algSet.header.algdb+e+'" target="_blank">'+l+"</a><p>";return d}function renderCase(c,b){var d="";var f=b>=PHONE_LANDSCAPE?"128":"96";var a=getCaseIds();d+="<h1>"+algSet.header.id+" "+c+"</h1>";d+=header();if(a.indexOf(c)>=0){var e=algSet.cases[a.indexOf(c)];if(e.name!=c){d+="<h2>"+e.name+"</h2>"}document.title=algSet.header.id+" "+c;d+='<p><i class="s'+f+"-"+algSet.header.id.toLowerCase()+" s"+f+"-"+e.image.toLowerCase()+'"></i></p>';if(e.desc){d+="<p>"+replaceAbbr(e.desc)+"</p>"}d+="<p>Probability = "+e.prob+"</p>";d+=renderCaseAlgs(e,b);d+=renderCaseLinks(e,b)}else{d+="Missing case "+c}d+=footer();document.getElementById("view").innerHTML=d}function switchCase(a){storeWindowOffset();var f="#case_"+a;try{var d={hash:f,xOffset:0,yOffset:0};var g=algSet.header.id+" "+a;var b=f;history.pushState(d,g,b);processHash()}catch(c){window.location.hash=f}window.scrollTo(0,0)};