function renderGridHeaderRow(a){var b="";b+="<thead>";b+="<tr>";b+="<th></th>";for(var c=0;c<a.headers.length;c++){var d=a.headers[c];b+="<th>"+d+"</th>"}b+="</tr>";b+="</thead>";return b}function renderGridDataRows(a){var d="";var f="64";var b=getCaseIds();d+="<tbody>";for(var g=0;g<a.rows.length;g++){var c=a.rows[g];d+="<tr>";d+='<th class="name">'+c.name+"</th>";for(var h=0;h<c.cases.length;h++){d+="<td>";if(c.cases[h]!=null){var e=algSet.cases[b.indexOf(c.cases[h])];if(e!=null){d+='<i class="clicky s'+f+"-"+algSet.header.id.toLowerCase()+" s"+f+"-"+e.image.toLowerCase()+'" onclick="switchCase(\''+e.id+"')\"/>"}}d+="</td>"}d+="</tr>"}d+="</tbody>";return d}function renderTableHeaderRow(a,f){var b="";b+="<thead>";b+="<tr>";if(f==false){b+="<th></th>"}b+="<th></th>";for(var c in a.uses){for(var e=0;e<algSet.header.uses.length;e++){var d=algSet.header.uses[e];if(d.id==a.uses[c]){b+='<th class="alg">'+d.name+"</th>"}if(f==true){break}}}if(f==false){b+='<th class="prob">P</th>'}b+="</tr>";b+="</thead>";return b}function renderViewAlg(d,e){var b="";var a=0;if(d.hasOwnProperty("status")){a=d.status}if(a==1){for(var c=0;c<d.uses.length;c++){if(d.uses[c]==e){b+=d.alg+"<br/>"}}}return b}function renderTableDataCell(b,f){var a="";for(var e=0;e<b.algs.length;e++){var c=b.algs[e];a+=renderViewAlg(c,f);if(c.hasOwnProperty("vars")){for(var g=0;g<c.vars.length;g++){var d=c.vars[g];a+=renderViewAlg(d,f)}}}if(a==""){a+="Missing case "+b.id+" for "+f}return a}function renderTableDataRows(i,f,e){var d="";var h=e==false?"96":"64";var b=getCaseIds();d+="<tbody>";for(var a=0;a<f.cases.length;a++){if(f.cases[a]!=null){var g=algSet.cases[b.indexOf(f.cases[a])];if(g!=null){d+="<tr>";if(e==false){d+="<td>"+g.id+"</td>"}d+='<td><i class="clicky s'+h+"-"+algSet.header.id.toLowerCase()+" s"+h+"-"+g.image.toLowerCase()+'" onclick="switchCase(\''+g.id+"')\"/></td>";for(var c in i.uses){d+="<td>";d+=renderTableDataCell(g,i.uses[c]);d+="</td>";if(e==true){break}}if(e==false){d+='<td class="prob">'+g.prob+"</td>"}d+="</tr>"}}}d+="</tbody>";return d}function renderViewOptions(b,e){var d="";d+='<p>Select view: <select id="viewList" onChange="switchView()"></p>';for(var c=0;c<algSet.views.length;c++){var a=algSet.views[c];if(a.id!="grid"||e==false){d+='<option value="'+a.id+'"';if(a.id==b){d+=" selected"}d+=">"+a.name+"</option>"}}d+="</select>";return d}function renderViewLinks(a){var b="";var d=0;b+="<p>";for(var e=0;e<a.groups.length;e++){var c=a.groups[e];if(e>0&&d>0){b+=", "}if(c.name!=""){b+='<a href="#'+a.id+"_"+c.id+'">'+c.name+"</a>"}d+=c.name.length;if(a.groups.length>8&&d>110){b+="</p><p>";d=0}}b+="</p>";return b}function renderView(b,h){var d="";if(b==""){b=algSet.views[0].id;if(b=="grid"&&h==true){b=algSet.views[1].id}}var f=false;d+="<h1>"+algSet.header.name+" ("+algSet.header.id+")</h1>";d+=important();d+=renderViewOptions(b,h);for(var c=0;c<algSet.views.length;c++){if(algSet.views[c].id==b){var a=algSet.views[c];if(a.name!=""){d+="<h2>"+a.name+"</h2>";document.title=algSet.header.id+" - "+a.name}if(a.hasOwnProperty("rows")){if(h==true){d+="<p>Sorry... Grid is too large for this display!</p>"}else{d+="<table>";d+=renderGridHeaderRow(a);d+=renderGridDataRows(a);d+="</table>"}}else{if(a.hasOwnProperty("groups")){if(h==false){d+=renderViewLinks(a)}for(var g=0;g<a.groups.length;g++){var e=a.groups[g];d+='<a name="'+a.id+"_"+e.id+'" />';if(e.name!=""){d+="<h3>"+e.name+"</h3>"}if(e.desc){d+="<p>"+replaceAbbr(e.desc)+"</p>"}d+="<table>";d+=renderTableHeaderRow(a,h);d+=renderTableDataRows(a,e,h);d+="</table>"}}}f=true}}if(f==false){d+="Missing view "+b}document.getElementById("view").innerHTML=d}function switchView(){storeWindowOffset();var c=document.getElementById("viewList");var a=c.options[c.selectedIndex].value;var g="#"+a;try{var f={hash:g,xOffset:window.pageXOffset,yOffset:window.pageYOffset};var h=algSet.header.id+" - "+c.options[c.selectedIndex].text;var b=g;history.pushState(f,h,b);processHash()}catch(d){window.location.hash=g}};