function renderGridHeaderRow(a,b){var c="";c+="<thead>";c+="<tr>";c+="<th></th>";for(var d=0;d<a.headers.length;d++){var e=a.headers[d];c+="<th>"+e+"</th>"}c+="</tr>";c+="</thead>";return c}function renderGridDataRows(i,e){var d="";var g=e>=TABLET_LANDSCAPE?"96":"64";var c=getCaseIds();d+="<tbody>";for(var b=0;b<i.rows.length;b++){var h=i.rows[b];d+="<tr>";d+='<th class="name">'+h.name+"</th>";for(var a=0;a<h.cases.length;a++){d+="<td>";if(h.cases[a]!=null){var f=algSet.cases[c.indexOf(h.cases[a])];if(f!=null){var j=algSet.header.id+" "+f.id;if(f.name!=f.id){j+=" - "+f.name}d+='<abbr title="'+j+'"><i class="clicky s'+g+"-"+algSet.header.id.toLowerCase()+" s"+g+"-"+f.image.toLowerCase()+'" onclick="switchCase(\''+f.id+"')\"/></abbr>"}}d+="</td>"}d+="</tr>"}d+="</tbody>";return d}function renderTableHeaderRow(a,b){var c="";if(b>=TABLET_LANDSCAPE){c+="<thead>";c+="<tr>";c+='<th class="id"></th>';c+="<th></th>";for(var d in a.uses){for(var f=0;f<algSet.header.uses.length;f++){var e=algSet.header.uses[f];if(e.id==a.uses[d]){c+='<th class="alg">'+e.name+"</th>"}}}c+='<th class="prob">P</th>';c+="</tr>";c+="</thead>"}return c}function renderViewAlg(d,a){var b="";if(a>=TABLET_LANDSCAPE){b+=d.alg+"<br/>"}else{b+=d.alg+" <sup>";for(var c=0;c<d.uses.length;c++){if(c>0){b+=", "}b+=d.uses[c]}b+="</sup><br/>"}return b}function renderTableDataCell(j,a,i){var g="";var d=[];var b=0;var c=i>=PHONE_LANDSCAPE?4:2;for(var f=0;f<j.algs.length&&b<c;f++){var l=j.algs[f];if(l.status==1){for(var e=0;e<l.uses.length;e++){if(a==null||l.uses[e]==a){if(i>=PHONE_LANDSCAPE||d.indexOf(l.uses[e])<0){g+=renderViewAlg(l,i);d=d.concat(l.uses);b++;break}}}}if(l.hasOwnProperty("vars")){for(var k=0;k<l.vars.length&&b<c;k++){var h=l.vars[k];if(h.status==1){for(var e=0;e<h.uses.length;e++){if(a==null||h.uses[e]==a){if(i>=PHONE_LANDSCAPE||d.indexOf(h.uses[e])<0){g+=renderViewAlg(h,i);d=d.concat(h.uses);b++;break}}}}}}}if(g==""){g+="Missing case "+j.id+" for "+a}return g}function renderTableDataRows(i,e,f){var d="";var h=f>=PHONE_LANDSCAPE?"96":"64";var b=getCaseIds();d+="<tbody>";for(var a=0;a<e.cases.length;a++){if(e.cases[a]!=null){var g=algSet.cases[b.indexOf(e.cases[a])];if(g!=null){d+="<tr>";if(f>=PHONE_LANDSCAPE){d+='<td class="id">'+g.id+"</td>"}var j=algSet.header.id+" "+g.id;if(g.name!=g.id){j+=" - "+g.name}d+='<td><abbr title="'+j+'"><i class="clicky s'+h+"-"+algSet.header.id.toLowerCase()+" s"+h+"-"+g.image.toLowerCase()+'" onclick="switchCase(\''+g.id+"')\"/></abbr></td>";if(f>=TABLET_LANDSCAPE){for(var c in i.uses){d+='<td class="alg">';d+=renderTableDataCell(g,i.uses[c],f);d+="</td>"}}else{d+='<td class="alg">';d+=renderTableDataCell(g,null,f);d+="</td>"}if(f>=PHONE_LANDSCAPE){d+='<td class="prob">'+g.prob+"</td>"}d+="</tr>"}}}d+="</tbody>";return d}function renderViewOptions(b,c){var e="";e+='<p>Select view: <select id="viewList" onChange="switchView()"></p>';for(var d=0;d<algSet.views.length;d++){var a=algSet.views[d];e+='<option value="'+a.id+'"';if(a.id==b){e+=" selected"}e+=">"+a.name+"</option>"}e+="</select>";return e}function renderViewLinks(a,b){var c="";var e=0;c+="<p>";for(var f=0;f<a.groups.length;f++){var d=a.groups[f];if(f>0&&e>0){c+=", "}if(d.name!=""){c+='<a href="#'+a.id+"_"+d.id+'">'+d.name+"</a>"}e+=d.name.length;if(a.groups.length>8&&e>110){c+="</p><p>";e=0}}c+="</p>";return c}function renderView(f,e){var b="";var i=false;for(var a=0;a<algSet.views.length;a++){if(algSet.views[a].id==f){i=true;break}}if(i==false){f=algSet.views[0].id}b+="<h1>"+algSet.header.name+" ("+algSet.header.id+")</h1>";b+=header();var c="<p>";if(f!="grid"){c+="This page lists the algorithms that I use during actual solves. They are good algorithms and have been chosen for their execution speed.</p><p>"}if(algSet.views.length>1){c+=" Use the dropdown below to switch views."}c+=" Click on an image for details about the case; e.g. algorithms, comments, breakdowns.</p>";b+=c;if(algSet.views.length>1){b+=renderViewOptions(f,e)}for(var a=0;a<algSet.views.length;a++){if(algSet.views[a].id==f){var h=algSet.views[a];if(h.name!=""){b+="<h2>"+h.name+"</h2>";document.title=algSet.header.id+" - "+h.name}if(h.hasOwnProperty("rows")){if(e<PHONE_LANDSCAPE){b+='<p class="alert">Rotate to view in landscape (horizontal) orientation</p>'}else{b+="<table>";b+=renderGridHeaderRow(h,e);b+=renderGridDataRows(h,e);b+="</table>"}}else{if(h.hasOwnProperty("groups")){b+=renderViewLinks(h,e);for(var g=0;g<h.groups.length;g++){var d=h.groups[g];b+='<a name="'+h.id+"_"+d.id+'" />';if(d.name!=""){b+="<h3>"+d.name+"</h3>"}if(d.desc){b+="<p>"+replaceAbbr(d.desc)+"</p>"}b+="<table>";b+=renderTableHeaderRow(h,e);b+=renderTableDataRows(h,d,e);b+="</table>"}}}i=true}}b+=footer();document.getElementById("view").innerHTML=b}function switchView(){storeWindowOffset();var c=document.getElementById("viewList");var a=c.options[c.selectedIndex].value;var g="#"+a;try{var f={hash:g,xOffset:window.pageXOffset,yOffset:window.pageYOffset};var h=algSet.header.id+" - "+c.options[c.selectedIndex].text;var b=g;history.pushState(f,h,b);processHash()}catch(d){window.location.hash=g}};