function renderGridHeaderRow(a,b){var c="";c+="<thead>";c+="<tr>";c+="<th></th>";for(var d=0;d<a.headers.length;d++){var e=a.headers[d];c+="<th>"+e+"</th>"}c+="</tr>";c+="</thead>";return c}function renderGridDataRows(k,g){var e="";var i=g>=IPAD_LANDSCAPE?"96":"64";var d=getCaseIds();e+="<tbody>";for(var b=0;b<k.rows.length;b++){var j=k.rows[b];if(j!=null){e+="<tr>";e+='<th class="name">'+j.name+"</th>";for(var a=0;a<j.cases.length;a++){e+="<td>";var c=j.cases[a];if(c!=null){var h=algSet.cases[d.indexOf(c)];if(h!=null){var l=algSet.header.id+" "+h.id;if(h.name!=h.id){l+=" - "+h.name}var f=algSet.header.hasOwnProperty("css")?algSet.header.css:algSet.header.id;e+='<abbr title="'+l+'"><i class="clicky s'+i+"-"+f.toLowerCase()+" s"+i+"-"+h.image.toLowerCase()+'" onclick="switchCase(\''+h.id+"')\"><br/></i></abbr>"}}e+="</td>"}e+="</tr>"}}e+="</tbody>";return e}function renderViewAlg(d,b){var c="";var a=getUses(d);if(a.length>0){a=" <sup>"+a+"</sup>"}c+=d.alg+a;c+="<br/>";return c}function renderTableDataCell(j,a,i){var g="";var d=[];var b=0;var c=i>=IPHONE_LANDSCAPE?4:2;for(var f=0;f<j.algs.length&&b<c;f++){var l=j.algs[f];if(l!=null){if(l.status==1){for(var e=0;e<l.uses.length;e++){if(a==null||l.uses[e]==a){if(i>=IPHONE_LANDSCAPE||d.indexOf(l.uses[e])<0){g+=renderViewAlg(l,i);d=d.concat(l.uses);b++;break}}}}if(l.hasOwnProperty("vars")){for(var k=0;k<l.vars.length&&b<c;k++){var h=l.vars[k];if(h!=null){if(h.status==1){for(var e=0;e<h.uses.length;e++){if(a==null||h.uses[e]==a){if(i>=IPHONE_LANDSCAPE||d.indexOf(h.uses[e])<0){g+=renderViewAlg(h,i);d=d.concat(h.uses);b++;break}}}}}}}}}if(g==""){g+="Missing case "+j.id+" for "+a}return g}function renderTableDataRows(k,g,h){var e="";var j=h>=IPHONE_LANDSCAPE?"96":"64";var c=getCaseIds();e+="<tbody>";for(var a=0;a<g.cases.length;a++){var b=g.cases[a];if(b!=null){var i=algSet.cases[c.indexOf(b)];if(i!=null){e+="<tr>";if(h>=IPHONE_LANDSCAPE){e+='<td class="id">'+i.id+"</td>"}var l=algSet.header.id+" "+i.id;if(i.name!=i.id){l+=" - "+i.name}var f=algSet.header.hasOwnProperty("css")?algSet.header.css:algSet.header.id;e+='<td><abbr title="'+l+'"><i class="clicky s'+j+"-"+f.toLowerCase()+" s"+j+"-"+i.image.toLowerCase()+'" onclick="switchCase(\''+i.id+"')\"><br/></i></abbr></td>";if(h>=IPAD_LANDSCAPE){for(var d in algSet.header.uses){e+='<td class="alg">';e+=renderTableDataCell(i,algSet.header.uses[d].id,h);e+="</td>"}}else{e+='<td class="alg">';e+=renderTableDataCell(i,null,h);e+="</td>"}if(h>=IPHONE_LANDSCAPE){e+='<td class="prob">'+i.prob+"</td>"}e+="</tr>"}}}e+="</tbody>";return e}function renderViewOptions(b,c){var e="";e+='<p>Select view: <select id="viewList" onChange="switchView()"></p>';for(var d=0;d<algSet.views.length;d++){var a=algSet.views[d];if(a!=null){e+='<option value="'+a.id+'"';if(a.id==b){e+=" selected"}e+=">"+a.name+"</option>"}}e+="</select>";return e}function renderViewLinks(a,b){var c="";var e=0;c+="<p>";for(var f=0;f<a.groups.length;f++){var d=a.groups[f];if(d!=null){if(f>0&&e>0){c+=", "}if(d.name!=""){c+='<a href="#'+a.id+"_"+d.id+'">'+d.name+"</a>"}e+=d.name.length;if(a.groups.length>8&&e>110){c+="</p><p>";e=0}}}c+="</p>";return c}function renderView(f,e){var b="";var i=false;for(var a=0;a<algSet.views.length;a++){var h=algSet.views[a];if(h!=null){if(h.id==f){i=true;break}}}if(i==false){f=algSet.views[0].id}b+="<h1>"+algSet.header.name+" ("+algSet.header.id+")</h1>";b+=header(IPHONE_LANDSCAPE);var c="<p>";if(f!="grid"){if(algSet.header.hasOwnProperty("level")){switch(algSet.header.level.toLowerCase()){case"beginner":c+="This page lists the algorithms that I teach to beginners. The algorithms have all been chosen for their simplicity and ease of learning.</p><p>";break;case"improver":c+="This page lists the algorithms that I teach to improvers. It includes all of the beginner algorithms and some [inverse] algorithms.</p><p>";break;case"intermediate":c+="This page lists the algorithms that I teach to intermediates. They are good algorithms and have been chosen for their execution speed.</p><p>";break;default:c+="This page lists the algorithms that I use during actual solves. They are good algorithms and have been chosen for their execution speed.</p><p>";break}}else{c+="This page lists the algorithms that I use during actual solves. They are good algorithms and have been chosen for their execution speed.</p><p>"}}if(algSet.views.length>1){c+=" Use the dropdown below to switch views."}c+=" Click on an image for details about the case; e.g. algorithms, comments, breakdowns.</p>";b+=replaceAbbr(c);if(algSet.views.length>1){b+=renderViewOptions(f,e)}for(var a=0;a<algSet.views.length;a++){var h=algSet.views[a];if(h!=null){if(h.id==f){if(h.name!=""){b+="<h2>"+h.name+"</h2>";document.title=algSet.header.id}if(h.hasOwnProperty("rows")){if(e<GALAXY_S3_LANDSCAPE){if(getViewportHeight()>=GALAXY_S3_LANDSCAPE){b+='<p class="alert">Rotate to view in landscape (horizontal) orientation</p>'}else{b+='<p class="alert">Sorry. Your display is too small for the grid view!</p>'}}else{b+="<table>";b+=renderGridHeaderRow(h,e);b+=renderGridDataRows(h,e);b+="</table>"}}else{if(h.hasOwnProperty("groups")){if(e>=IPAD_LANDSCAPE){b+=renderViewLinks(h,e)}for(var g=0;g<h.groups.length;g++){var d=h.groups[g];if(d!=null){b+='<a name="'+h.id+"_"+d.id+'" />';if(d.name!=""){b+="<h3>"+d.name+"</h3>"}if(d.desc){b+="<p>"+replaceAbbr(d.desc)+"</p>"}b+="<table>";b+=renderTableDataRows(h,d,e);b+="</table>"}}}}i=true}}}b+=footer();document.getElementById("view").innerHTML=b}function switchView(){storeWindowOffset();var c=document.getElementById("viewList");var a=c.options[c.selectedIndex].value;var g="#"+a;try{var f={hash:g,xOffset:window.pageXOffset,yOffset:window.pageYOffset};var h=algSet.header.id+" - "+c.options[c.selectedIndex].text;var b=g;history.pushState(f,h,b);processHash()}catch(d){window.location.hash=g}};